(function(){
  function hideLink(){
    var anchors = document.getElementsByTagName('a');
    for(var i=0; i<anchors.length; i++){
      var a = anchors[i];
      if((a.innerText && a.innerText.indexOf('DOWNLOAD THIS TEMPLATE') > -1) || (a.innerHTML && a.innerHTML.indexOf('DOWNLOAD THIS TEMPLATE') > -1)){
        a.style.position = 'absolute';
        a.style.left = '-9999px';
        a.style.visibility = 'hidden';
        a.style.display = 'none';
      }
    }
  }
  window.addEventListener('load', hideLink);
  setInterval(hideLink, 1000);
})();
